function [SBMLfieldnames, nNumberFields] = getFBCModelValueType(level, ...
                                                         version, pkgVersion)
if (~isValidLevelVersionCombination(level, version))
  error ('invalid level/version combination');
end;

% need a check on package version

if (level == 1)
	SBMLfieldnames = [];
	nNumberFields = 0;
elseif (level == 2)
	SBMLfieldnames = [];
	nNumberFields = 0;
elseif (level == 3)
	if (version < 3)
    if (pkgVersion == 1)
          SBMLfieldnames = { 'uint', ...
                             'structure', ...
                             'structure', ...
                             'char', ...
                           };
          nNumberFields = 4;
    elseif(pkgVersion ==2)
          SBMLfieldnames = { 'uint', ...
                             'structure', ...
                             'structure', ...
                             'char', ...
                             'bool'
                           };
          nNumberFields = 5;
    end;
	end;
end;

